import React, { Component } from "react";
import { connect } from "react-redux";
import { Link } from 'react-router-dom';
import './Buscador.css';
import {addMovieFavorite,getMovies,removeMovieFavorite,getMovieDetail} from '../../actions/index'


var removeChildren = function (qs) {
  var selector = document.querySelector(qs);
  while (selector.firstChild) {
      selector.removeChild(selector.firstChild);
  }; 
}

export class Buscador extends Component {
  constructor(props) {
    super(props);
    this.state = {
      title: ""
    };
  }


/*   addList(array) {
    removeChildren('#moviesList')
    for(let i=0;i<array.length;i++) {
      var moviesList = document.getElementById('moviesList');
      var nombre = document.createElement('li');  
      var fav = document.createElement('button');
      nombre.innerHTML = `<Link to=${`/movie/${array[i].imdbID}`}>${array[i].Title}</Link>`;
      fav.onclick=
      moviesList.append(nombre);
    }
  } */

  handleChange(event) {
    this.setState({ title: event.target.value });

  }

  handleSubmit(event) {
    event.preventDefault();
  /*   console.log(this.state.title);   */
    this.props.getMovies(this.state.title);
    /* this.addList(this.props.movies); */
    this.props.movies && this.props.movies.map(movie =>
      <div key={movie.imdbID}>
        <h4>{movie.Title}</h4>
      </div>
      )
  }

  render() {
    const { title } = this.state;
    return (
      <div>
        <h2>Buscador</h2>
        <form className="form-container" onSubmit={(e) => this.handleSubmit(e)}>
          <div>
            <label className="label" htmlFor="title">Pel√≠cula: </label>
            <input
              type="text"
              id="title"
              autoComplete="off"
              value={title}
              onChange={(e) => this.handleChange(e)}
            />
          </div>
          <button type="submit">BUSCAR</button>
        </form>
        <ul id='moviesList'>
         {/* Aqui tienes que escribir tu codigo para mostrar la lista de peliculas */}
        </ul>
      </div>
    );
  }
}

function mapStateToProps(state) {
  return {
    movies: state.moviesLoaded,
    /* favorites: state.moviesFavourites,
    detail: state.movieDetail */
  };
}

function mapDispatchToProps(dispatch) {
  return {
    addMovieFavorite: movie => dispatch(addMovieFavorite(movie)),
    getMovies: title => dispatch(getMovies(title)),
    removeMovieFavorite: movie => dispatch(removeMovieFavorite(movie)),
    getMovieDetail: id => dispatch(getMovieDetail(id))
  };
}

export default connect(
  mapStateToProps,
  mapDispatchToProps
)(Buscador);
