import React, { Component } from "react";
import { connect } from "react-redux";
import { Link } from 'react-router-dom';
import './Favorites.css';
import {removeMovieFavorite} from '../../actions/index'

export class ConnectedList extends Component {

  render() {
    return (
      <div className='moviesList'>
        <ul className ='movieCard'>
          {
            this.props.favorites && this.props.favorites.map(movie =>
              <div className='fav'>
                <Link to={`/movie/${movie.imdbID}`}>
                <img src={movie.Poster}/> 
                </Link>
                <button onClick={() => this.props.removeMovieFavorite(movie.imdbID)}>X</button>
              </div>
              )
          }
        </ul>
      </div>
    );
  }
}

function mapStateToProps(state) {
  return {
/*     movies: state.moviesLoaded, */
    favorites: state.moviesFavourites,
   /*  detail: state.movieDetail */
  };
}

function mapDispatchToProps(dispatch) {
  return {
    /* addMovieFavorite: movie => dispatch(addMovieFavorite(movie)),
    getMovies: title => dispatch(getMovies(title)), */
    removeMovieFavorite: idMovie => dispatch(removeMovieFavorite(idMovie)),
/*     getMovieDetail: id => dispatch(getMovieDetail(id)) */
  };
}

export default connect(
  mapStateToProps,
  mapDispatchToProps
)(ConnectedList);
