import React, { Component } from "react";
import { connect } from "react-redux";
import { Link } from 'react-router-dom';
import {addMovieFavorite,getMovies,removeMovieFavorite,getMovieDetail} from '../../actions/index'
import './Home.css'



export default class Home extends Component {
  constructor(props) {
    super(props);
    this.state = {
      title: ""
    };
  }

  render() {
    return (
      <div className='title'>
        <h1>Welcome to Juan Martin's movie app!</h1>
        <p>Welcome here! This is Juan Martin's movie app. Here you will be able to access to detailed information about your favorite movies. You can also fav the movies you like in order to add them as your favorite movies, or make a personalized wishlist!</p>

      </div>
    );
  }
}
