const initialState = {
  moviesFavourites: getLocalStorage(),
  moviesLoaded: [],
  movieDetail: {},
};

function getLocalStorage() {
  const data = window.localStorage.getItem("movies");
  return data ? JSON.parse(data) : [];
}

function setLocalStorage(movie) {
  window.localStorage.setItem("movies", JSON.stringify(movie));
}

function rootReducer(state = initialState, action) {
    if (action.type === 'ADD_MOVIE_FAVORITE') {
      const fav_movie = state.moviesFavourites.concat(action.payload);
      setLocalStorage(fav_movie);
        return {
          ...state,
          moviesFavourites: fav_movie,
        }
    } else if (action.type === 'GET_MOVIES') {
        return {
          ...state,
          moviesLoaded: action.payload.Search
        };
    } else if (action.type === 'GET_MOVIE_DETAIL') {
        return {
            ...state,
            movieDetail: action.payload
        }
    } else if (action.type === 'REMOVE_MOVIE_FAVORITE'){
      const remove_movie = state.moviesFavourites.filter(movies => movies.imdbID !== action.payload);
      setLocalStorage(remove_movie);
        return {
            ...state,
            moviesFavourites: remove_movie,
        }
    } else {
    return state;
    }
  }


  export default rootReducer;
