import React, { Component } from "react";
import { connect } from "react-redux";
import { Link } from 'react-router-dom';
import {addMovieFavorite,getMovies,removeMovieFavorite,getMovieDetail} from '../../actions/index'
import './Home.css';
import {logo} from '../../movieLogo.png';



export default class Home extends Component {
  constructor(props) {
    super(props);
    this.state = {
      title: ""
    };
  }

  render() {
    return (
      <div className='contenedor'>
          <div className='title'>
        <h1>Welcome to Juan Martin's MovieApp!</h1>
        <p>Welcome here! This is Juan Martin's MovieApp. Here you will be able to access to detailed information about your favorite movies. You can also fav the movies you like in order to add them as your favorite movies, or make a personalized wishlist!</p>
          </div>   
          <img src={logo}/>
      </div>
    );
  }
}
