import {ADD_MOVIE_FAVORITE , GET_MOVIES , GET_MOVIE_DETAIL , REMOVE_MOVIE_FAVORITE} from '../actions/index'

const initialState = {
    moviesFavourites: [],
    moviesLoaded: [],
    movieDetail: {}
  };

export function rootReducer(state = initialState, action) {
    if (action.type === ADD_MOVIE_FAVORITE) {
        return {
          ...state,
          moviesFavourites: state.moviesFavourites.concat(action.payload)
        }
    } else if (action.type === GET_MOVIES) {
        return {
          ...state,
          moviesLoaded: action.payload.Search
        };
    } else if (action.type === GET_MOVIE_DETAIL) {
        return {
            ...state,
            movieDetail: action.payload.Search
        }
    } else if (action.type === REMOVE_MOVIE_FAVORITE){
        return {
            ...state,
            moviesFavourites: state.moviesFavourites.filter(movies => movies.imdbId !== action.payload.imdbId)
        }
    } else {
    return state;
    }
  }


  export default rootReducer;
