import React, { Component } from "react";
import { connect } from "react-redux";
import { Link } from 'react-router-dom';
import './Favorites.css';
import {addMovieFavorite,getMovieDetail,getMovies,removeMovieFavorite} from '../../actions/index'

export class ConnectedList extends Component {

  render() {
    return (
      <div>
        <h2>Pel√≠culas Favoritas</h2>
        <ul>
          {
            this.props.favorites && this.props.favorites.map(movie =>
              <div>
                <Link to={`/movie/${movie.imdbID}`}>
                <span>{movie.Title}</span> 
                </Link>
                <button onClick={() => this.props.removeMovieFavorite(movie)}>X</button>
              </div>
              )
          }
        </ul>
      </div>
    );
  }
}

function mapStateToProps(state) {
  return {
/*     movies: state.moviesLoaded, */
    favorites: state.moviesFavourites,
   /*  detail: state.movieDetail */
  };
}

function mapDispatchToProps(dispatch) {
  return {
    /* addMovieFavorite: movie => dispatch(addMovieFavorite(movie)),
    getMovies: title => dispatch(getMovies(title)), */
    removeMovieFavorite: idMovie => dispatch(removeMovieFavorite(idMovie)),
/*     getMovieDetail: id => dispatch(getMovieDetail(id)) */
  };
}

export default connect(
  mapStateToProps,
  mapDispatchToProps
)(ConnectedList);
